                'title' => 'Peran Laboratorium Tersertifikasi Bagi Perkembangan Industri Benih Indonesia',
                'slug' => 'peran-laboratorium-tersertifikasi-bagi-perkembangan-industri-benih-indonesia',
                'metadesc' => NULL,
